'From Pharo3.0 of 18 March 2013 [Latest update: #30847] on 11 September 2014 at 12:02:04.663534 pm'!!GLMUIThemeExtraIcons class methodsFor: 'utils' stamp: 'PhilippeBack 9/11/2014 10:58'!reverseIcons	"self reverseIcons"	"An attempt to reverse icons"	icons do: [ :icon | icon replaceColor: Color transparent withColor: Color white ].! !!LazyTabGroupMorph methodsFor: 'adding' stamp: 'PhilippeBack 9/11/2014 11:43'!addLazyPage: aBlock label: aStringOrMorph toolbar: aToolbar collapsable: aBoolean	| newPage label innerLabel |	newPage := LazyTabPage new.	newPage		lazyPageMorphCreation: aBlock; 		labelMorph: aStringOrMorph; 		toolbar: aToolbar.	innerLabel := aStringOrMorph isMorph		ifTrue: [aStringOrMorph]		ifFalse: [			|labelMorph|			labelMorph := GLMButton new disable.			labelMorph label text: aStringOrMorph.			labelMorph color: Color white.			labelMorph		].	label := aBoolean 			ifTrue: [UITheme builder newRow: { 				innerLabel .				UITheme builder					newCloseControlFor: nil					action: [self removePage: newPage]					help: nil}]			ifFalse: [innerLabel].	self 		addPage: newPage		label: label.! !!RubCursor methodsFor: 'accessing' stamp: 'PhilippeBack 9/11/2014 11:53'!visibleCursorColor	^ self class cursorWithFixedColor 		ifTrue: [ self class defaultColor ]		ifFalse: [ self textArea backgroundColor contrastingColor alpha: 0.6 ]! !